import 'package:chatbase_app/Screens/Chat_Details_Screen/chat_details_screen.dart';
import 'package:chatbase_app/Styles/textstyle.dart';
import 'package:chatbase_app/constants.dart';
import 'package:flutter/material.dart';

class ChatUserList extends StatelessWidget {
  final String firstName;
  final String lastName;
  final String chatText;
  final String profileImage;
  final String time;
  final bool isMessageRead;
  const ChatUserList({
    Key? key,
    required this.firstName,
    required this.lastName,
    required this.chatText,
    required this.profileImage,
    required this.time,
    required this.isMessageRead,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      onTap: () {
        Navigator.of(context)
            .push(MaterialPageRoute(builder: (context) => const ChatDetails()));
      },
      child: Container(
        // color: Colors.red,
        child: Row(
          children: [
            Expanded(
              child: Row(
                children: [
                  Container(
                    decoration: BoxDecoration(
                      border: Border.all(color: Colors.black38, width: 1.0),
                      borderRadius: BorderRadius.circular(100.0),
                    ),
                    child: CircleAvatar(
                      backgroundImage: AssetImage(profileImage),
                      maxRadius: 28.0,
                    ),
                  ),
                  const SizedBox(width: 16.0),
                  Expanded(
                    child: Container(
                      color: Colors.blue,
                      child: Column(
                        crossAxisAlignment: CrossAxisAlignment.start,
                        children: [
                          Text(
                            firstName + " " + lastName,
                            maxLines: 1,
                            style: const TextStyle(
                              color: Colors.black87,
                              fontSize: 16.0,
                              fontWeight: FontWeight.w800,
                              overflow: TextOverflow.ellipsis,
                            ),
                          ),
                          const SizedBox(height: 6.0),
                          Text(
                            chatText,
                            maxLines: 1,
                            style: TextStyle(
                              color: isMessageRead
                                  ? Colors.black54
                                  : Colors.black45,
                              fontSize: 14.0,
                              overflow: TextOverflow.ellipsis,
                              fontWeight: isMessageRead
                                  ? FontWeight.w600
                                  : FontWeight.w400,
                            ),
                          ),
                        ],
                      ),
                    ),
                  ),
                ],
              ),
            ),
            Text(
              time,
              style: TextStyle(
                fontSize: 12.0,
                color: isMessageRead ? kPrimaryColor : Colors.grey.shade600,
                fontWeight: isMessageRead ? FontWeight.w600 : FontWeight.normal,
              ),
            ),
          ],
        ),
      ),
    );
  }
}
